import movieSchema from '../models/movies'

// Importar las bibliotecas necesarias
const mongoose = require('mongoose');


// Crear el modelo de película
const Movie = mongoose.model('Movie', movieSchema);

// Función para obtener todas las películas
const getAllMovies = async (req, res) => {
  try {
    const peliculas = await Movie.find();
    res.json(peliculas);
  } catch (error) {
    res.status(500).json({ message: error.message });
  }
};

// Función para obtener una película por su ID
const getMovieById = async (req, res) => {
  try {
    const pelicula = await Movie.findOne({ id_movie: req.params.id });
    if (pelicula) {
      res.json(pelicula);
    } else {
      res.status(404).json({ message: 'Película no encontrada' });
    }
  } catch (error) {
    res.status(500).json({ message: error.message });
  }
};

// Función para actualizar una película por su ID
const updateMovie = async (req, res) => {
  try {
    // Lógica para actualizar una película
    const updatedMovie = await Movie.findOneAndUpdate(
      { id_movie: req.params.id },
      { $set: req.body },
      { new: true }
    );
    res.json(updatedMovie);
  } catch (error) {
    res.status(500).json({ message: error.message });
  }
};

// Función para eliminar una película por su ID
const deleteMovie = async (req, res) => {
  try {
    // Lógica para eliminar una película
    const deletedMovie = await Movie.findOneAndDelete({ id_movie: req.params.id });
    res.json(deletedMovie);
  } catch (error) {
    res.status(500).json({ message: error.message });
  }
};

// Exportar las funciones para su uso en las rutas
module.exports = {
  getAllMovies,
  getMovieById,
  updateMovie,
  deleteMovie,
};

